mapper.insert:
   		INSERT INTO visa_admin_info(email, first_name, middle_name, last_name) 
   		VALUES (#{email},#{first_name},#{middle_name},#{last_name});

		INSERT INTO visa_login(email,password,role_id)VALUES(#{email},#{password},#{role_id});

mapper.select:
SELECT COUNT(*) > 0
FROM visa_user_info
WHERE email = #{email}
